package com.arushkharbanda;

import java.util.Date;
import java.util.LinkedList;

class Subsequence {
    public int start;
    public String sequenceStr;
    public int length;

    Subsequence(int start, String sequenceStr, int length)
    {
        this.start=start;
        this.sequenceStr=sequenceStr;
        this.length=length;
    }
}

public class LongestPalindrome {

    public String longestPalindrome(String s) {
        LinkedList<Subsequence> ln=new LinkedList();
        Subsequence longest=new Subsequence(0,"",0);
        for(int i=0;i<s.length(); i++)
        {
            Subsequence subsequence=new Subsequence(i,s.charAt(i)+"",1);
            ln.add(subsequence);
        }
        while(! ln.isEmpty())
        {
            Subsequence sub=ln.pop();
            if (longest!=null)
            {
                if (longest.length<sub.length)
                {
                    longest=sub;
                }
            }
            else
            {
                longest=sub;
            }

            int previous=sub.start-1;
            int next=sub.start+ sub.length;

            if(sub.length==1) {
                if (previous >= 0) {
                    String newStr = s.charAt(previous) + sub.sequenceStr;
                    if (newStr.charAt(0)==newStr.charAt(newStr.length()-1)) {
                        ln.add(new Subsequence(previous, newStr, newStr.length()));
                    }

                }

                if (next < s.length()) {
                    String newStr = sub.sequenceStr + s.charAt(next);
                    if (newStr.charAt(0)==newStr.charAt(newStr.length()-1)) {
                        ln.add(new Subsequence(sub.start, newStr, newStr.length()));
                    }
                }
            }

            if ( next< s.length()  && previous>=0 )
            {
                String newStr=s.charAt(previous)+sub.sequenceStr+s.charAt(next);

                if (newStr.charAt(0)==newStr.charAt(newStr.length()-1))
                {

                    ln.add(new Subsequence(previous, newStr,newStr.length()));
                }

            }


        }
        return longest.sequenceStr;


    }

    public boolean isPalindrome(String s)
    {

        int l=s.length();
        for (int i=0; i<s.length()/2;i++)
        {
            if(s.charAt(i)!=s.charAt(l-i-1))
                return false;
        }
        return true;
    }


    public static void main(String args[])
    {

        check("babad","bab");
        check("bbbbb","bbbb");
        check("cbbd","bb");
        check("abcdefedcba","abcdefedcba");
        check("abcdefedcbaasdadas","abcdefedcba");
        check("","");
        check("","");
        check("","");
        check("","");
        check("","");


    }

    public static void check(String a, String b)
    {
        LongestPalindrome sol=new LongestPalindrome();
        long start= new Date().getTime();
        String output=sol.longestPalindrome(a);
        long end= new Date().getTime();
        long time=end-start;
        print(String.format("output %s expected %s in %d",a,b, time));
        assert(a==b);


    }

    public static void print(String s)
    {
        System.out.println(s);
    }
}
